import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';


//the chat application
class App extends Component{
  constructor(props){
    super(props);
    this.state={};
    this.login=this.login.bind(this);
  }

  componentDidMount(){
    this.setState({hi : 'me'});
  }

  login(){
    this.setState(function(prev){
        return {hi : 'logged'+prev.hi};});
  }

  render() {
    return (
    <div className="App">
      <header className="App-header">
        <p>Token is {window.token} Hi</p>
      </header>
    </div>
  );
  }
}

export default App;
