import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

function fetchWith(name, params){
  return fetch(name, {method: 'POST', 
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(params)})
    .then(res => res.json());
}

//the chat application
class App extends Component{
  constructor(props){
    super(props);
    this.state={welcomeMessage : ""};
    this.login=this.login.bind(this);
  }

  componentDidMount(){
    this.setState({welcomeMessage : "...loading message..."});
    fetchWith('/get-data', {type: "client"})
    .then(data => this.setState({welcomeMessage : data.message}));
  }

  login(){
    this.setState(function(prev){
        return {welcomeMessage : prev.welcomeMessage + " User"};});
  }

  render() {
    return (
        <p>Token is {window.token} Hi {this.state.welcomeMessage}</p>
  );
  }
}

export default App;
