import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

function fetchWith(name, params){
  return fetch(name, {method: 'POST', 
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(params)})
    .then(res => res.json());
}

class InputBox extends Component{
  constructor(props){
    super(props);
    this.state={value : ""};
    this.handleChange=this.handleChange.bind(this);
  }

  handleChange(){
    var app=this, val=event.target.value;
    app.setState(function(prev){
      if(val.length> 1000) return {};
      return {value: val};
    });
  }

  render(){
    return (<div className={styles.login}><form onSubmit={event => 
      this.props.login(this.state.value)}> <label> Username: {" "} 
      <input type="text" value={this.state.value} name="username"
          onChange={this.handleChange} placeholder="your username"/> 
      </label>
      </form></div>)
  }
}

//the dashboard application
class App extends Component{
  constructor(props){
    super(props);
    this.state={welcomeMessage : "",
                username : ""};
    this.login=this.login.bind(this);
  }

  componentDidMount(){
    this.setState({welcomeMessage : "loading..."});
    fetchWith('/get-data', {type: "client"})
    .then(data => this.setState({welcomeMessage : data.message}));
  }

  login(name){
    fetchWith('/login', {type: name})
    .then(res => this.setState({username : name}));
  }

  render() {
    return (<div class="App">
        <p>{window.token}</p>
        <p>...{this.state.welcomeMessage} {this.state.Username}...</p>
        <InputBox login={this.login} />
        </div>
  );
  }
}

export default App;
