import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

function fetchWith(name, params){
  return fetch(name, {method: 'POST', 
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(params)})
    .then(res => res.json());
}

