import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

function fetchWith(name, mtd, params){
  if(mtd === 'POST' || mtd === 'DELETE') {
    return fetch(name, {method: mtd, 
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(params)})
      .then(res => res.json());
    }
    return fetch(name, {method: mtd})
      .then(res => res.json());
}

class InputBox extends Component{
  constructor(props){
    super(props);
    this.state={name : "", time: "", kind: ""};
    this.handleChange=this.handleChange.bind(this);
  }

  handleChange(event){
    var app=this, val=event.target.value;
    app.setState(function(prev){
      if(val.length> 1000) return {};
      return {value: val};
    });
  }

  render(){
    return (<div><form onSubmit={event => 
      this.props.addAppt(this.state, event)}> <label> Username: {" "} 
      <input type="text" value={this.state.value} name="username"
          onChange={this.handleChange} placeholder="your username"/> 
      </label>
      </form></div>)
  }
}

//the dashboard application
class App extends Component{
  constructor(props){
    super(props);
    this.state={doctorData : {}};
    this.addAppt=this.addAppt.bind(this);
  }

  componentDidMount(){
    this.setState({welcomeMessage : "loading..."});
    fetchWith('/get-data', 'GET', {})
    .then(data => this.setState({doctorData : data.doctors}));
  }

  addAppt(params, event){
    event.preventDefault();
    this.setState(function(prev){
      prev.doctorData[params.dr].appts[params.date] = params.appt
      return {doctorData : prev.doctors}})
    fetchWith('/login', params);
  }

  render() {
    var doctorList = Object.entries(this.state.doctorData);
    return (<div class="App">
        <p>{window.token}</p>
        <ul>{doctorList.map(item => 
          (<li eventKey = {item[0]}> {item[1].lname}, {item[1].fname} </li>))}</ul>
        <InputBox login={this.addAppt} />
        </div>
  );
  }
}

export default App;
