import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

function fetchWith(name, mtd, params){
  if(mtd === 'POST' || mtd === 'DELETE') {
    return fetch(name, {method: mtd, 
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(params)})
      .then(res => res.json());
    }
    return fetch(name, {method: mtd})
      .then(res => res.json());
}

class InputBox extends Component{
  constructor(props){
    super(props);
    this.state={fname : "", lname : "", time: "", kind: ""};
    this.handleChange=this.handleChange.bind(this);
  }

  render(){
    return (<div><form onSubmit={event => 
      this.props.addAppt(this, event)}> <label> Username: {" "} 
      <input type="text" value={this.state.fname} name="First Name"
          onChange={event => this.setState({fname : event.target.value})} 
          placeholder="First Name"/> 
      <input type="text" value={this.state.lname} name="Last Name"
          onChange={event => this.setState({lname : event.target.value})} 
          placeholder="Last Name"/> 
      <input type="text" value={this.state.time} name="Time"
          onChange={event => this.setState({time : event.target.value})} 
          placeholder="Time"/>
      <input type="text" value={this.state.kind} name="Kind"
          onChange={event => this.setState({kind : event.target.value})} 
          placeholder="Kind"/> 
      </label>
      </form></div>)
  }
}

//the dashboard application
class App extends Component{
  constructor(props){
    super(props);
    this.state={doctorData : {},
                viewDoctor: "",
                viewDate: "",
                dateTxt: "",
                newApptID: 0};
    this.addAppt=this.addAppt.bind(this);
  }

  componentDidMount(){
    this.setState({welcomeMessage : "loading..."});
    fetchWith('/get-data', 'GET', {})
    .then(data => this.setState({doctorData : data.doctors}));
  }

  addAppt(comp, event){
    event.preventDefault();
    param = comp.state;
    params.apptID = this.state.newApptID;
    this.setState(function(prev){
      prev.doctorData[params.dr].appts[params.date] = params.appt
      return {doctorData : prev.doctors, newApptID : prev.newApptID + 1}})
    fetchWith('/add-appt', params).then(res => comp.setState({fname : "", lname : "", time: "", kind: ""}));
  }

  render() {
    var doctorList = Object.entries(this.state.doctorData);
    var app = this;
    var DoctorDisplay = "";
    var doc = null, scheds;
    if(app.state.viewDoctor in app.state.doctorData){
      var doc = app.state.doctorData[app.state.viewDoctor];
      var schedDisplay = "";
      if(app.state.viewDate in doc.appts){
        scheds = doc.appts[app.state.viewDate];
        schedDisplay = (<div>
            <InputBox login={this.addAppt} /></div>);
      }
      DoctorDisplay = (<div>
        <h2>Dr. {doc.fname} {doc.lname}</h2>
        <h3>{doc.email}</h3>
        <form onSubmit={event => 
      this.setState(function(prev){return {viewDate : prev.dateTxt};})}> 
      <label> Date: {" "} 
      <input type="text" value={this.state.value} name="date"
          onChange={event => this.setState({dateTxt: event.target.value})} placeholder="enter date"/> 
      </label>
      </form>
        {schedDisplay}</div>)
    }
    return (<div>
      <ul>{doctorList.map(function(item){
          var selected = ""
          if(item[0] == app.state.viewDoctor){
            selected = '(X)'
          }
          return (<li eventKey = {item[0]}> <a onClick={(event)=>
          app.setState({viewDoctor : item[0]})}>
              {item[1].lname}, {item[1].fname} </a> {selected} </li>)})}</ul>
      <div class="App">
        <h1>{window.token}</h1>
        {DoctorDisplay}
        </div></div>
  );
  }
}

export default App;
